#  Error Codes are specific and unique codes for a very specific problem.

INVALID_CREDENTIALS_CODE = 4001 # Typically used when someone tries to logins and fails!
INVALID_USERNAME_CODE = 4010 # When the registration username is not valid for some reason!
INVALID_USERNAME_TOO_SHORT_CODE = 4011 # When the registration username is too short!
INVALID_EMAIL_CODE    = 4020 # When the email is not valid for some reason!
INVALID_EMAIL_FORMAT_CODE    = 4020 # The email does not comply with standard URL formats
INVALID_PASSWORD_CODE = 4030 # Bad Password...
